/**
 * Générateur de mèmes (React single-file)
 * --------------------------------------------------
 * - Un composant React prêt à l'emploi (export default)
 * - Utilise Tailwind CSS pour le style (aucun import Tailwind ici,
 *   assume ton projet a Tailwind configuré)
 * - Fonctionnalités :
 *   - Téléverser une image depuis l'ordinateur
 *   - Ajouter plusieurs calques de texte (édition, police, taille, couleur)
 *   - Aperçu en temps réel via <canvas>
 *   - Positionner les textes par glisser-déposer
 *   - Télécharger en PNG
 *   - Partager via Web Share API ou liens réseaux sociaux
 *   - Galerie (stockée en localStorage)
 *
 * Usage :
 * - Colle ce fichier dans ton projet React (par ex. src/components/MemeGenerator.jsx)
 * - Assure-toi que Tailwind est configuré dans ton projet (classes utilitaires utilisées)
 * - Importe et utilise <MemeGenerator /> dans ton app
 */

import React, { useRef, useState, useEffect } from "react";

export default function MemeGenerator() {
  const canvasRef = useRef(null);
  const imageRef = useRef(null);

  const [imageFile, setImageFile] = useState(null);
  const [imageObject, setImageObject] = useState(null);
  const [texts, setTexts] = useState([createEmptyText()]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [gallery, setGallery] = useState(loadGallery());

  // Canvas size (will adapt to image size, with max dims)
  const MAX_WIDTH = 1000;
  const MAX_HEIGHT = 1000;

  // ————— helpers —————
  function createEmptyText() {
    return {
      id: Date.now() + Math.random(),
      text: "",
      x: 50,
      y: 50,
      fontSize: 40,
      fontFamily: "Impact, Arial, sans-serif",
      color: "#ffffff",
      stroke: "#000000",
      align: "center",
      bold: true,
      uppercase: true,
    };
  }

  function loadGallery() {
    try {
      const raw = localStorage.getItem("meme_gallery_v1");
      return raw ? JSON.parse(raw) : [];
    } catch (e) {
      return [];
    }
  }

  function saveGallery(arr) {
    localStorage.setItem("meme_gallery_v1", JSON.stringify(arr));
  }

  // ————— image upload —————
  function onFileChange(e) {
    const file = e.target.files && e.target.files[0];
    if (!file) return;
    setImageFile(file);
    const url = URL.createObjectURL(file);
    const img = new Image();
    img.onload = () => setImageObject(img);
    img.src = url;
  }

  // ————— drawing —————
  useEffect(() => {
    drawCanvas();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [imageObject, texts]);

  function computeCanvasSize(img) {
    if (!img) return { w: 800, h: 450 };
    let { width: w, height: h } = img;
    const ratio = Math.min(MAX_WIDTH / w, MAX_HEIGHT / h, 1);
    return { w: Math.round(w * ratio), h: Math.round(h * ratio) };
  }

  function drawCanvas() {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");

    if (!imageObject) {
      // placeholder
      canvas.width = 800;
      canvas.height = 450;
      ctx.fillStyle = "#333";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      ctx.fillStyle = "#fff";
      ctx.font = "20px Arial";
      ctx.fillText("Téléverse une image pour commencer", 20, 40);
      return;
    }

    const size = computeCanvasSize(imageObject);
    canvas.width = size.w;
    canvas.height = size.h;

    // draw image
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(imageObject, 0, 0, canvas.width, canvas.height);

    // draw texts
    texts.forEach((t, i) => {
      const fontSpec = `${t.bold ? "700" : "400"} ${t.fontSize}px ${t.fontFamily}`;
      ctx.font = fontSpec;
      ctx.textAlign = t.align;
      ctx.lineJoin = "round";
      ctx.fillStyle = t.color;
      ctx.strokeStyle = t.stroke;
      ctx.lineWidth = Math.max(2, Math.round(t.fontSize / 12));
      ctx.textBaseline = "top";
      const x = (t.x / 100) * canvas.width; // store as percent for responsiveness
      const y = (t.y / 100) * canvas.height;

      const displayText = t.uppercase ? t.text.toUpperCase() : t.text;
      // stroke then fill for meme-style outline
      ctx.strokeText(displayText, x, y);
      ctx.fillText(displayText, x, y);

      // optional: draw bounding box for active text
      if (i === activeIndex) {
        const metrics = ctx.measureText(displayText);
        const height = t.fontSize * 1.2;
        const width = metrics.width;
        ctx.save();
        ctx.strokeStyle = "rgba(255,255,255,0.7)";
        ctx.setLineDash([6, 4]);
        ctx.strokeRect(x - width / 2 - 6, y - 4, width + 12, height + 8);
        ctx.restore();
      }
    });
  }

  // ————— text controls —————
  function updateText(idx, patch) {
    setTexts((prev) => {
      const next = prev.map((t, i) => (i === idx ? { ...t, ...patch } : t));
      return next;
    });
  }

  function addText() {
    setTexts((p) => [...p, createEmptyText()]);
    setActiveIndex(texts.length);
  }

  function removeText(idx) {
    if (texts.length === 1) return setTexts([createEmptyText()]);
    setTexts((p) => p.filter((_, i) => i !== idx));
    setActiveIndex(0);
  }

  // ————— drag to move —————
  function handlePointerDown(e) {
    if (!imageObject) return;
    const canvas = canvasRef.current;
    if (!canvas) return;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // detect nearest text within bounds
    const ctx = canvas.getContext("2d");
    let found = -1;
    for (let i = texts.length - 1; i >= 0; i--) {
      const t = texts[i];
      const displayText = t.uppercase ? t.text.toUpperCase() : t.text;
      ctx.font = `${t.bold ? "700" : "400"} ${t.fontSize}px ${t.fontFamily}`;
      const metrics = ctx.measureText(displayText);
      const tx = (t.x / 100) * canvas.width;
      const ty = (t.y / 100) * canvas.height;
      const w = metrics.width;
      const h = t.fontSize * 1.2;
      if (x >= tx - w / 2 - 6 && x <= tx + w / 2 + 6 && y >= ty - 6 && y <= ty + h + 6) {
        found = i;
        break;
      }
    }

    if (found >= 0) {
      setActiveIndex(found);
      setIsDragging(true);
      setOffset({ x, y });
    }
  }

  function handlePointerMove(e) {
    if (!isDragging) return;
    const canvas = canvasRef.current;
    if (!canvas) return;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const dx = x - offset.x;
    const dy = y - offset.y;
    setOffset({ x, y });

    setTexts((prev) => {
      const copy = [...prev];
      const t = copy[activeIndex];
      if (!t) return prev;
      const newX = t.x + (dx / canvas.width) * 100;
      const newY = t.y + (dy / canvas.height) * 100;
      copy[activeIndex] = { ...t, x: clamp(newX, 0, 100), y: clamp(newY, 0, 100) };
      return copy;
    });
  }

  function handlePointerUp() {
    setIsDragging(false);
  }

  function clamp(v, a, b) {
    return Math.max(a, Math.min(b, v));
  }

  // ————— export / share —————
  function downloadMeme(filename = "meme.png") {
    const canvas = canvasRef.current;
    if (!canvas) return;
    canvas.toBlob((blob) => {
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = filename;
      a.click();
      URL.revokeObjectURL(url);
    });
  }

  async function shareMeme() {
    const canvas = canvasRef.current;
    if (!canvas) return;
    if (navigator.canShare && navigator.canShare()) {
      const blob = await new Promise((res) => canvas.toBlob(res));
      const file = new File([blob], "meme.png", { type: "image/png" });
      try {
        await navigator.share({ files: [file], title: "Mon mème" });
        return;
      } catch (e) {
        console.warn("Web Share échoué", e);
      }
    }
    // fallback: open image in new tab
    const data = canvas.toDataURL("image/png");
    const w = window.open();
    if (w) w.document.body.innerHTML = `<img src='${data}' alt='meme'/>`;
  }

  function saveToGallery() {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const data = canvas.toDataURL("image/png");
    const item = {
      id: Date.now(),
      data,
      createdAt: new Date().toISOString(),
    };
    const next = [item, ...gallery].slice(0, 24);
    setGallery(next);
    saveGallery(next);
  }

  function removeFromGallery(id) {
    const next = gallery.filter((g) => g.id !== id);
    setGallery(next);
    saveGallery(next);
  }

  // ————— UI —————
  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Générateur de mèmes</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Left: controls */}
        <div className="md:col-span-1 space-y-4">
          <div className="bg-white p-4 rounded shadow">
            <label className="block text-sm font-medium mb-2">Image</label>
            <input type="file" accept="image/*" onChange={onFileChange} />
            {imageFile && (
              <div className="mt-2 text-xs text-gray-600">{imageFile.name}</div>
            )}

            <div className="mt-4">
              <button
                className="px-3 py-2 bg-blue-600 text-white rounded mr-2"
                onClick={() => {
                  setImageFile(null);
                  setImageObject(null);
                }}
              >
                Réinitialiser
              </button>
              <button
                className="px-3 py-2 bg-green-600 text-white rounded"
                onClick={() => addText()}
              >
                Ajouter un calque texte
              </button>
            </div>
          </div>

          <div className="bg-white p-4 rounded shadow">
            <label className="block text-sm font-medium mb-2">Calques de texte</label>
            <div className="space-y-2 max-h-64 overflow-auto">
              {texts.map((t, i) => (
                <div
                  key={t.id}
                  className={`p-2 border rounded ${i === activeIndex ? "border-blue-500" : "border-gray-200"}`}
                  onClick={() => setActiveIndex(i)}
                >
                  <div className="flex justify-between items-center">
                    <strong className="text-sm">Calque #{i + 1}</strong>
                    <div className="flex gap-1">
                      <button
                        className="text-xs px-2 py-1 bg-red-500 text-white rounded"
                        onClick={() => removeText(i)}
                      >
                        Suppr
                      </button>
                    </div>
                  </div>
                  <input
                    className="mt-2 w-full border p-1 text-sm rounded"
                    placeholder="Texte..."
                    value={t.text}
                    onChange={(e) => updateText(i, { text: e.target.value })}
                  />

                  <div className="mt-2 grid grid-cols-2 gap-2 text-sm">
                    <label className="flex flex-col">
                      Taille
                      <input
                        type="range"
                        min="12"
                        max="120"
                        value={t.fontSize}
                        onChange={(e) => updateText(i, { fontSize: Number(e.target.value) })}
                      />
                    </label>

                    <label className="flex flex-col">
                      Align
                      <select
                        value={t.align}
                        onChange={(e) => updateText(i, { align: e.target.value })}
                      >
                        <option value="left">gauche</option>
                        <option value="center">centre</option>
                        <option value="right">droite</option>
                      </select>
                    </label>

                    <label className="flex flex-col">
                      Couleur
                      <input
                        type="color"
                        value={t.color}
                        onChange={(e) => updateText(i, { color: e.target.value })}
                      />
                    </label>

                    <label className="flex flex-col">
                      Contour
                      <input
                        type="color"
                        value={t.stroke}
                        onChange={(e) => updateText(i, { stroke: e.target.value })}
                      />
                    </label>

                    <label className="flex items-center gap-2">
                      <input
                        type="checkbox"
                        checked={t.uppercase}
                        onChange={(e) => updateText(i, { uppercase: e.target.checked })}
                      />
                      Majuscules
                    </label>

                    <label className="flex items-center gap-2">
                      <input
                        type="checkbox"
                        checked={t.bold}
                        onChange={(e) => updateText(i, { bold: e.target.checked })}
                      />
                      Gras
                    </label>
                  </div>
                </div>
              ))}
            </div>

            <div className="mt-3 text-xs text-gray-600">Clique sur un calque puis déplace-le sur la zone d'aperçu (glisser-déposer).</div>
          </div>

          <div className="bg-white p-4 rounded shadow">
            <label className="block text-sm font-medium mb-2">Actions</label>
            <div className="flex gap-2 flex-wrap">
              <button
                className="px-3 py-2 bg-indigo-600 text-white rounded"
                onClick={() => downloadMeme("meme.png")}
              >
                Télécharger
              </button>
              <button
                className="px-3 py-2 bg-orange-600 text-white rounded"
                onClick={() => shareMeme()}
              >
                Partager
              </button>
              <button
                className="px-3 py-2 bg-yellow-500 text-black rounded"
                onClick={() => saveToGallery()}
              >
                Sauvegarder dans la galerie
              </button>
            </div>
          </div>
        </div>

        {/* Center: canvas preview */}
        <div className="md:col-span-2">
          <div className="bg-white p-4 rounded shadow">
            <div className="flex justify-between items-center mb-2">
              <div>
                <strong>Aperçu</strong>
                <div className="text-xs text-gray-600">Clique et déplace le texte sur l'image</div>
              </div>
              <div className="text-sm text-gray-500">Sélection : {activeIndex + 1}</div>
            </div>

            <div
              className="border rounded overflow-hidden touch-none"
              onPointerDown={handlePointerDown}
              onPointerMove={handlePointerMove}
              onPointerUp={handlePointerUp}
              onPointerCancel={handlePointerUp}
              style={{ cursor: isDragging ? "grabbing" : "grab" }}
            >
              <canvas ref={canvasRef} className="w-full block" />
            </div>

            <div className="mt-3 text-xs text-gray-600">Tip: utilise la molette/les sliders pour régler la taille du texte.</div>
          </div>

          {/* Gallery preview small */}
          <div className="bg-white p-4 rounded shadow mt-4">
            <div className="flex items-center justify-between mb-2">
              <strong>Galerie (local)</strong>
              <div className="text-sm text-gray-500">{gallery.length} élément(s)</div>
            </div>
            <div className="grid grid-cols-3 md:grid-cols-6 gap-2">
              {gallery.length === 0 && <div className="text-xs text-gray-500">Aucun mème enregistré</div>}
              {gallery.map((g) => (
                <div key={g.id} className="relative border rounded overflow-hidden">
                  <img src={g.data} alt="meme" className="w-full h-24 object-cover" />
                  <div className="absolute top-1 right-1 flex gap-1">
                    <button
                      className="text-xs bg-white/80 rounded px-1"
                      onClick={() => {
                        const w = window.open();
                        if (w) w.document.body.innerHTML = `<img src='${g.data}'/>`;
                      }}
                    >
                      Ouvrir
                    </button>
                    <button
                      className="text-xs bg-white/80 rounded px-1"
                      onClick={() => removeFromGallery(g.id)}
                    >
                      Suppr
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="mt-6 text-sm text-gray-600">Conseils : utilise des polices lourdes (Impact), grandes tailles et contour sombre pour l'effet "mème" classique.</div>
    </div>
  );
}
