import React, { useRef, useState, useEffect } from "react";

export default function MemeGenerator() {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");

  // Téléversement d'image
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const img = new Image();
    img.src = URL.createObjectURL(file);
    img.onload = () => setImage(img);
  };

  // Dessiner le mème
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    if (!image) {
      canvas.width = 800;
      canvas.height = 450;
      ctx.fillStyle = "#333";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      ctx.fillStyle = "#fff";
      ctx.font = "20px Arial";
      ctx.fillText("Téléverse une image pour commencer", 20, 40);
      return;
    }
    // Adapter taille
    const maxW = 1000, maxH = 1000;
    const ratio = Math.min(maxW / image.width, maxH / image.height, 1);
    canvas.width = image.width * ratio;
    canvas.height = image.height * ratio;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(image, 0, 0, canvas.width, canvas.height);

    // Texte
    ctx.fillStyle = "#fff";
    ctx.strokeStyle = "#000";
    ctx.lineWidth = 3;
    ctx.textAlign = "center";
    ctx.font = "bold 40px Impact";
    ctx.textBaseline = "top";
    if (topText) {
      ctx.strokeText(topText.toUpperCase(), canvas.width / 2, 10);
      ctx.fillText(topText.toUpperCase(), canvas.width / 2, 10);
    }
    if (bottomText) {
      ctx.textBaseline = "bottom";
      ctx.strokeText(bottomText.toUpperCase(), canvas.width / 2, canvas.height - 10);
      ctx.fillText(bottomText.toUpperCase(), canvas.width / 2, canvas.height - 10);
    }
  }, [image, topText, bottomText]);

  // Télécharger
  const downloadMeme = () => {
    const canvas = canvasRef.current;
    const link = document.createElement("a");
    link.download = "meme.png";
    link.href = canvas.toDataURL("image/png");
    link.click();
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Générateur de mèmes simple</h1>
      <input type="file" accept="image/*" onChange={handleImageUpload} className="mb-2"/>
      <input
        type="text"
        placeholder="Texte du haut"
        value={topText}
        onChange={(e) => setTopText(e.target.value)}
        className="border p-1 w-full mb-2"
      />
      <input
        type="text"
        placeholder="Texte du bas"
        value={bottomText}
        onChange={(e) => setBottomText(e.target.value)}
        className="border p-1 w-full mb-2"
      />
      <button onClick={downloadMeme} className="bg-blue-600 text-white px-3 py-1 rounded mb-4">
        Télécharger
      </button>
      <canvas ref={canvasRef} className="border w-full"></canvas>
    </div>
  );
}
